// An optical illusion I got from a flyer for "Professor Smart"
// http://www.professorsmart.info/science-shows-for-schools
//
// BUT IT DOESN'T WORK ON THE SCREEN.
// Presumably because my display only goes up to 60Hz.
//
// The effect should be a distinct grey disk in the middle of the spinning coin.


#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))

const float pi   = 3.14159265359;

// parameters
const float zoom       = 0.5;
const float background = 1.0;

void mainImage( out vec4 RGBA, in vec2 XY )
{
    vec2 xy = (XY * 2.0 - RES) / MINRES / zoom;

    float t = texelFetch(iChannel0, ivec2(0), 0).r;
    t *= 25.0;    

    xy += 0.4 * vec2(cos(t), sin(t));

    float angle       = atan(xy.y, xy.x);
    float repeated    = mod(angle * 10.0, pi * 2.0);
    float u           = cos(repeated);

    float r   = length(xy);

    float c   = smoothstep(0.0, 1.2 - r, u);


    float eps = 4.0 / MINRES / zoom;

    c = mix(c, 0.0             , smoothstep(-eps, eps, r - 1.0));
    c = mix(c, background * 0.5, smoothstep(-eps, eps, r - 1.03));
    c = mix(c, background      , smoothstep(-eps, eps, r - 1.06));

    RGBA.rgb = vec3(c);
    RGBA.a   = 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = texelFetch(iChannel0, ivec2(0), 0).r;
    float f = texelFetch(iChannel0, ivec2(0), 0).g;

    float target = iMouse.z < 1.0 ? 1.0 : 0.0;

    f = clamp(0.0, 1.0, mix(f, target, 8.0 * iTimeDelta));
    t += iTimeDelta * f;

    fragColor = vec4(t, f, 0.0, 0.0);
}
