float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;


    vec3 col = vec3(0.);
    uv *= 1.2;

    const float max_iter = 80.0;

    vec2 z = uv / 1.0;
    vec2 mouse = iMouse.xy / iResolution.xy * 2.0 - 1.0;

    float angle = mouse.x * 2.0 * 3.1416 * iTime * 0.3 ;

    float i = 0.0;

    float r = random(uv);
    vec2 mx = vec2(sin(angle), cos(angle));

    vec2 c = mouse;


    for(i = 0.; i < max_iter; i+= 1.) {
        z = vec2((z.x * z.x) - (z.y * z.y), 2.0 * z.x * z.y) + c;

        if(length(z) > 2.) break;
    }

    float f = i / max_iter;
    f = pow(f, 0.87);

    col += mix(vec3(0.0,0.0,0.0), vec3(0.0,1.0, 0.0), f);

    fragColor = vec4(col,1.0);
}
