vec3 palette( in float t ) {
    vec3 a = vec3(0.969,0.807,0.489);
    vec3 b = vec3(0.966,0.695,0.156);
    vec3 c = vec3(0.908,1.763,1.99);
    vec3 d = vec3(5.559,2.149,1.484);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ((fragCoord / iResolution.xy) - 0.5) * 2.0;
    vec2 mouse = ((iMouse.xy / iResolution.xy) - 0.5) * 2.0;

    float pos = -abs(sin(uv.x * 6.0 - iTime * 6.0 + uv.x)) + 1.0;

    vec3 split = vec3(mouse.x / uv.xy, 1.0) * 0.2;
    vec3 col = palette(uv.x * uv.y + pos) + palette(mouse.x * mouse.y - pos) + split;

    fragColor = vec4(col * pos + uv.y + sin(iTime), 1.0);
}
