#define PI 3.1415926538421


vec3 SKY_BLUE=vec3(0.467,0.792,0.929);
vec3 SCARLET=vec3(1.,0.141,0.);
vec3 EGGSHELL=vec3(0.941,0.918,0.839)*0.975;
vec3 CORAL=vec3(1.,0.435,0.38);

float swishes(vec2 uv){
    float swish=uv.y+cos(mod(-uv.x,3.))*0.5-0.5;
    float swish2=uv.y+cos(mod(-uv.x*2., 3.))*0.5-0.5;
    float swish3=uv.y+cos(mod(-uv.x+1.5,3.))*0.5-0.5;

    return step(swish,0.5)*1.-(step(swish2,0.5)+-step(swish3,0.5));
}
vec2 circle(vec2 uv){
    float circle=length(uv-vec2(0.5,0.5))*2.;
    float circle_a = atan(uv.y-0.5,uv.x-0.5);
    return vec2(0.5+(circle_a/(PI * 2.0)),circle);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv*=15.;
    uv.y*=0.35;
    uv.y-=3.5;
    //uv.x+=iTime;
    float swish=swishes(uv);
    uv*=2.;uv.y-=1.5;uv.x+=0.5;
    float swish2=swishes(uv);
    uv*=2.;uv.y*=0.5;uv.y-=0.9;uv.x+=2.9;uv.x*=0.5;
    float swish3=swishes(uv);

    // Time varying pixel color

    vec3 col;
    //col = vec3(swish);
    uv = fragCoord/iResolution.xy;
    uv.x+=iTime*0.08;
    uv.y*=18.;
    uv.y+=sin(uv.x*80.)*0.1*sin(iTime*0.15);
    uv.y/=18.;
    uv*=10.;

    float sky_distort=(uv.y/5.)*(cos(fract(uv.x*2.5)-0.5)*abs(fract(uv.y-0.5)-0.5));


    uv = fragCoord/iResolution.xy;
    vec3 sky_grad=mix(SKY_BLUE,mix(SCARLET,CORAL,sky_distort), -0.2+uv.y*1.3); 

    uv = fragCoord/iResolution.xy;
    uv*=15.;
    uv.x*=2.;
    uv.y*=0.5;
    uv.y-=1.1;
    float hills=fract(abs(sin(uv.x)-5.)*1.)+uv.y;
    hills*=fract(abs(sin(uv.x-PI)-5.)*0.8)+uv.y;
    hills=smoothstep(0.55,0.6,hills);

    uv.y+=0.4;
    uv.y*=0.6;
    //uv.x-=5.*(PI/2.)+0.01;
    float hills2=fract(abs(sin(uv.x)-5.)*0.5)+uv.y;
    hills2*=fract(abs(sin(uv.x-PI)-5.)*0.5)+uv.y;
    hills2=smoothstep(0.59,0.6,hills2);




    uv = fragCoord/iResolution.xy;
    uv.y/=iResolution.x/iResolution.y;uv.y+=0.21;

    uv*=0.9;
    uv+=vec2(0.05,0.05);
    vec2 c=circle(uv+vec2(0.0,0.0));
    c*=5.*PI;

    vec2 c2=c;
    c2.y+=abs(sin(c.x*2.*tan(0.065*iTime))-0.4);
    c2.y-=3.5;
    float god_mask=step(0.2,c2.y*0.5);

    uv.y*=3.8;uv.y-=0.55;
    vec2 c3=circle(uv);
    float shadow=step(0.2,c3.y);

    c.x+=fract(sin(c.y*5.*sin(0.1*iTime)));
    float god=step(0.5,fract((c.x*1.4)*PI*0.1));

    col=sky_grad;
    col=mix(vec3(god),col,god_mask);

    col=mix(SKY_BLUE, col, swish);

    col=mix(CORAL,col,swish2);
    col=mix(CORAL,col,hills);
    col=mix(SKY_BLUE,col,swish3);
    col=mix(SCARLET,col,hills2);
    uv = fragCoord/iResolution.xy;


    vec2 c_a=circle(uv+vec2(0.0,0.2));

    float floor_mask=step(fract(c_a.x*95.),0.5);
    vec3 floor_=mix(vec3(1.),EGGSHELL, floor_mask);
    col=mix(col,floor_,step(uv.y,0.15));
    col=mix(vec3(0.),col,shadow);


    //col=vec3(0.,0.,god_mask);
    //col=vec3(0.,hills2,hills);
    //col=vec3(sky_distort);
    // Output to screen
    fragColor = vec4(col,1.0);
}
