#define rot(r) mat2(cos(r), -sin(r), sin(r), cos(r))
#define T iTime

void mainImage(out vec4 o, vec2 u) {    
    vec3 p = iResolution; 
    u = (u - p.xy / 2.) / p.y;  
    o -= o;

    float i, e, d, a;
    while(i++ < 1e2){
        p = vec3(u * a, a - 3.);

        p.xy *= rot(T*1.5);
        p.zy *= rot(T);

        p.y *= d = .2;

        p *= e = 9./dot(p, p);

        d *= e;

        p.xz *= rot(p.y * .3 + T * 5.);

        int q;
        while(q++ < 3)
            p.x = abs(p.x),
            p.xz *= rot(3.14/3.);

        a += e = sin(p.x / 1e2) * 4. / d  +  .4 / d;

        o.z += .01/exp(5e2 * e);
        o.xy += (.008 + .003 * cos(p.z * 1.)) / exp(1e3 * e);
    }
}
