void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = vec2(0.5);
    vec2 point = uv - center;;
    float speed = .2;
    float pi = 3.141592;
    float strength = 1. - length( uv- center) * 1.5;

    point = vec2( 
        point.x * cos(pi * (iTime ) * speed * strength) - point.y * sin(pi * (iTime ) * speed *  strength),
        point.x * sin(pi * (iTime ) * speed  * strength) + point.y * cos(pi * (iTime ) * speed * strength)
       );

    vec3 col = texture(iChannel0, point).xyz;

    fragColor = vec4(col,1.0);
}
