float f_n_rand(
    vec2 o
){
    return fract(sin(o.x*33134.5543+o.y*3121.32112)*12.23*3232.33);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_coo_nor = ((fragCoord.xy) - (iResolution.xy)*0.5) /(iResolution.y);
    vec2 o_mou_coo_nor = ((iMouse.xy) - (iResolution.xy)*0.5) /(iResolution.y);
    if(iMouse.z == 0.0){
    o_mou_coo_nor = vec2(0.35,0.0);
    }
    float n_ds = 1./(99.*o_mou_coo_nor.x);
    vec2 o_fragCoord_ds = floor(fragCoord * n_ds);
    vec3 o_iRes_ds = floor(iResolution * n_ds);
    vec2 o_pix_coo_nor_ds = ((o_fragCoord_ds.xy) - (o_iRes_ds.xy * 0.5)) / o_iRes_ds.y; 

    float n_dist = length(o_pix_coo_nor_ds);
    fragColor.gb = (o_pix_coo_nor_ds);
    fragColor.r = n_dist;
    fragColor = vec4(n_dist);

    if(o_pix_coo_nor.x > o_pix_coo_nor.y){
        fragColor = vec4(length(o_pix_coo_nor));
    }
}
