void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    // vec2 uv = fragCoord/iResolution.xy;

    float lineWidth = 1.0;
    float speed = 6.0;
    if (fragCoord.x >= (iResolution.x - lineWidth) / 2.0 && 
        fragCoord.x <= (iResolution.x + lineWidth) / 2.0) {
        fragColor = vec4(0.0);
    } 
    else if (fragCoord.y >= (iResolution.y - lineWidth) / 2.0 &&
        fragCoord.y <= (iResolution.y + lineWidth) / 2.0) {
        fragColor = vec4(0.0);   
    }
    else {

        float peak = 80.0;
        float pi = 3.1415926;
        float v = sin(pi * 2.0 * (fragCoord.x / peak / 4.0) - iTime * speed) * peak;
        float y = floor(v + iResolution.y / 2.0);
        float minY = v + iResolution.y / 2.0 - lineWidth;
        float maxY = v + iResolution.y / 2.0 + lineWidth;
        if (fragCoord.y >= minY && fragCoord.y <= maxY) {
            fragColor = vec4(0.0);
        } else {
            fragColor = vec4(1.0);
        }
    }


}
