// from draft base https://shadertoy.com/view/dtySD1

#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) ) 
#define cinv(Z)   ( vec2( (Z).x, -(Z).y ) / dot(Z,Z) ) 

void mainImage( out vec4 O, vec2 u )
{
    O *= 0.;
    for ( int k=0; k < 9; k++ ) {                  // AA loop

        vec2  R = iResolution.xy,
              d = vec2(k%3-1, k/3-1) / 3.,         // AA oversampling
              z = -2.45* ( 2.*(u+d) - R )  / R.y,  // windowing
              c = vec2(1.0546875,.4453125); 
        float r = 4., N = 26.;          

        for ( float i = 0. ; i < N && dot(z,z) < r; i++ )  // Julia loop
            z = cmul ( c ,  z + cinv(z) );

        O += vec4( z.y < 0. );                     // binary decomposition
    }
    O = sqrt(O/9.);                                // to sRGB

 // O = vec4( sqrt(.5 - .5* z.y / fwidth(z.y)) );  // variant without the AA loop
}
