float func(vec2 p){
    p += sin(p*4.*3.14159 + iTime)*.03;

    p -= iTime*.06;

    return smoothstep(.7 - iTime*.006, 1., texture(iChannel0, p).x);
}

void mainImage( out vec4 O, vec2 U )
{
    U = .5 * U/iResolution.xy;

    O = vec4(0)+func(U);
    O += func(U*vec2(-1,1));
    O += func(U*vec2(1,-1));
    O += func(-U);
}
