float spiralWave(vec2 p, float ratio, float scale) {

    float r = length(p);

    float theta = atan(p.x,p.y);

    float logspiral = log(r)/log(ratio) + theta;

    return sin(scale*logspiral);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;

    vec2 p = (fragCoord - 0.5*iResolution.xy) / iResolution.y;

    float goldenRatio = 1.618034;

    float d = spiralWave(p*32.0f*(time*time),goldenRatio,1.0);  

    float hd = 0.5f * fwidth(d);

    float gray = smoothstep(-hd, hd, d);

    vec3 color = vec3( gray  );

 	fragColor  = vec4(color,1.0);
}
