#define pi2 6.28318530718
#define e(t,a,b) exp((t)*(a))*vec2(sin(pi2*(b)*(t)),sin(pi2*(b)*(t))) 
vec2 mainSound( int samp, float time )
{
    #define MEASURE (3. + sin(.1*time))
    time += 10.*MEASURE;
    vec2 o = vec2(0);
    for (float i = 0.; i < 4.; i++) {
        vec4 h1 = hash41(floor(time/MEASURE));
        float t = mod(time,MEASURE);
        o += .25*e(t+e(t,-1.,100.*h1.x).x/(100.*h1.x),-1.,440.*h1.y)*exp(-2.*h1.y);
        time += MEASURE/4.;
    }
    return o;
}

vec4 hash41(float p)
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
float tri ( vec2 p, float h ) {
  vec2 q = abs(p);
  return max(q.x*0.866025+p.y*0.5,-p.y)-h*0.5;
}
float SDF (vec2 p) {
    float d = tri(p,.1);

    d = min(d,tri((p-vec2(-.25,.25))*vec2(1,-1),.1));
    d = min(d,tri((p-vec2(+.25,.25))*vec2(1,-1),.1));

    d = min(d,
        max(
            max(
                max(
                    length(p-vec2(0,0.))-.4,
                    .2+p.y
                ),
                -tri((p-vec2(.15,-.2))*vec2(1,-1),.1)
            ),
            -tri((p-vec2(-.15,-.2))*vec2(1,-1),.1)
        )
    );

    return d;
}

Main {
    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    Q.z -= 0.25*(e.x-w.x+n.y-s.y);
}
