void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.* fragCoord.xy - iResolution.xy) / min(iResolution.x,iResolution.y);
    vec4 col;
    //grid
    float GridSize = 5.;
    vec2 cell= fract(uv * GridSize);
    vec2 LineWidth = fwidth(uv * GridSize);
    if(cell.x < LineWidth.x)
    {
        col = vec4(1.);
    }
    if(cell.y < LineWidth.y)
    {
        col = vec4(1.);
    }

    //axis X and axis Y
    if(abs(uv.x) < LineWidth.x / GridSize * 2.5)
    {
        col = vec4(0.,0.5,0.,1.);
    }

    if(abs(uv.y) < LineWidth.y / GridSize * 2.5)
    {
        col = vec4(0.,0.5,0.,1.);
    }

    fragColor = col;
}
