void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

    vec2 uv0  = (fragCoord * iResolution.xy)/iResolution.x-.5 + iResolution.y -.5 ;

    float d = 1.0 / fract(length(uv)) + fract(length(uv0));

    d += fract(tan(fract(d) / fract(iTime)));

    float sm  = smoothstep(0.5,1.0,d);

    float dSm = d + sm;

    vec3 col = cos(iTime + dSm + vec3(5.0));

    vec3 final = col + fract(vec3(1.0,0.0,0.0));

    fragColor = vec4(vec3(final),1.0);
}
