#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define MAX_ITER 500u
#define MAX_DIST 1000.0
#define EPSILON 0.001

float halfspace(vec3 p, float d)
{
    return p.z - d;
}

float box(vec3 p, vec3 b) // https://iquilezles.org/articles/distfunctions/
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// modified https://www.shadertoy.com/view/NsKSDc Truncated Pyramid SDF by TheTurk
float pyramid(vec3 p, vec2 base, float halfHeight)
{
    p.xy = abs(p.xy); // symmetry
    p.z -= halfHeight;

    float s1 = abs(p.z) - halfHeight;
    // bottom
    vec3 base1 = vec3(max(p.xy - base, vec2(0)), abs(p.z + halfHeight));
    float d1 = dot(base1, base1);
    // top
    vec3 base2 = vec3(max(p.xy, vec2(0)), abs(p.z - halfHeight));
    float d2 = dot(base2, base2);

    vec3 point1 = vec3(base,-halfHeight);
    vec3 point2 = vec3(0,0,+halfHeight);
    vec3 position1 = p - point1;
    vec3 position2 = p - point2;
    vec3 end = point2 - point1;

    vec3 segment = position1 - end * clamp(dot(position1, end) / dot(end, end), 0.0, 1.0);
    float d = dot(segment, segment);

    vec3 normal1 = vec3(+end.z, 0.0,-end.x);
    float s2 = dot(position1, normal1);
    float d3 = d;
    if (dot(position1.xz,-end.xz) < 0.0 &&
        dot(position2.xz,+end.xz) < 0.0 &&
        dot(position1, cross(normal1,-end)) < 0.0)
    {
        d3 = s2 * s2 / dot(normal1, normal1);
    }

    vec3 normal2 = vec3(0.0,+end.z,-end.y);
    float s3 = dot(position1, normal2);
    float d4 = d;
    if (dot(position1.yz,-end.yz) < 0.0 &&
        dot(position2.yz,+end.yz) < 0.0 &&
        dot(position1, cross(normal2,+end)) < 0.0)
    {
        d4 = s3 * s3 / dot(normal2, normal2);
    }

    return sqrt(min(min(min(d1, d2), d3), d4)) * sign(max(max(s1, s2), s3));
}

float rhombic_dodecahedron_roofs(vec3 p)
{
    float t = 0.4 * (0.5 + 0.5 * cos(2.0 * pi * iTime / 10.0));
    float d = MAX_DIST;
    // d = halfspace(p,-2.5);
    d = min(d, box(p, vec3(1)));
    p = abs(p); // reflection symmetry
    // rotational symmetry around diagonal
    if(p.x > p.z && p.x > p.y) p = p.yzx;
    if(p.y > p.z && p.y > p.x) p = p.zxy;
    d = min(d, pyramid(p-vec3(0,0,1.0+t), vec2(1), 0.5));
    return d;
}

float map(vec3 p)
{
    return rhombic_dodecahedron_roofs(p);
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h)
            +
            k.yyx * map(p + k.yyx*h)
            +
            k.yxy * map(p + k.yxy*h)
            +
            k.xxx * map(p + k.xxx*h)
        );
}

float trace(vec3 ro, vec3 rd)
{
    float t;
    uint i;
    for(i = 0u, t = 0.0; i < MAX_ITER && t < MAX_DIST; i++)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return MAX_DIST;
}

// https://iquilezles.org/articles/rmshadows
float shadow(in vec3 ro, in vec3 rd, float mint, float maxt)
{
    float t;
    uint i;
    for(i = 0u, t = mint; i < MAX_ITER && t < maxt; i++)
    {
        float h = map(ro + rd*t);
        if(h < EPSILON)
            return 0.0;
        t += h;
    }
    return 1.0;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives by iq
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

float checker(vec3 p)
{
    return step(0.0, sin(pi * p.x + pi/2.0)*sin(pi *p.y + pi/2.0)*sin(pi *p.z + pi/2.0));
}

void mainImage(out vec4 fragColor, in vec2 I)
{
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y;
    vec3 rd = normalize(vec3(I.x, 2.0, I.y)); // must be normalized!
    vec3 ro = vec3(0.0,-5.0, 0.0);

    float mx = -2.0 * pi * float(iMouse.x) / float(iResolution.x);
    float my = -pi / 2.0 + pi * float(iMouse.y) / float(iResolution.y);
    mx = (iMouse.x > 5.0) ? mx : +pi / 4.0 + 2.0 * pi * fract(iTime * 0.01);
    my = (iMouse.y > 5.0) ? my : -pi / 6.0;

    mat3 M = yaw_pitch_roll(mx, my, 0.0);
    ro = M * ro;
    rd = M * rd;

    vec3 lightdir = normalize(vec3(4.0, 2.0, 1.0));
    vec3 color = vec3(0.0);

    float dist = trace(ro, rd);
    if(dist < MAX_DIST)
    {
        vec3 dst = ro + rd * dist;
        vec3 n = normal(dst);

        vec3 ambient = vec3(0.02);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0) brightness *= shadow(dst+0.01*n,lightdir, 0.0, MAX_DIST);
        color = vec3(1.0);
        // hack to color surface
        if(dst.x <= -1.0) color = vec3(1,0,0);
        if(dst.x >= +1.0) color = vec3(0,1,1);
        if(dst.y <= -1.0) color = vec3(0,1,0);
        if(dst.y >= +1.0) color = vec3(1,0,1);
        if(dst.z <= -1.0) color = vec3(0,0,1);
        if(dst.z >= +1.0) color = vec3(1,1,0);

        color = mix(color, (n * 0.5 + 0.5), 0.25); // normal colors
        color = (ambient * calcAO(dst, n) + brightness) * color;
    }
    else
    {
        vec3 sky = vec3(0); // black sky
        // sky = mix(vec3(0.2,0.5,1), vec3(0.0,0.0,0.5), rd.z); // blue sky
        color = sky;

        color += smoothstep(0.998, 0.999, dot(lightdir, rd)); // sun
        color += 0.5 * smoothstep(0.998, 1.0, dot(lightdir, rd)); // halo
    }

    fragColor = vec4(sqrt(color), 1.0); // approximate gamma
}
