// inverted round Union

// SDF primitives taken from:
// and iquilezles.org/articles/distfunctions2d


float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

float sdCircle2( in vec2 p, in vec2 o, in float r ) 
{
    return length(p-o)-r;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}


vec2 mirror2Dv2(in vec2 p, in vec2 N)
{
    float proj = min(dot(p, N), 0.0);
    return p - 2.0*N*proj;
}

float sdinvCircle(in vec2 p, in float r)
{
    // two lines with space for corner
    vec2 q = p;
    q = mirror2Dv2(q, normalize(vec2(1,1)));
    q.y -= r;
    q.y = min(0.0, q.y);
    float ad = sign(q.x)*length(q);

    // inverted round corner
    vec2 s = vec2(min(p.x, 0.0), max(p.y, 0.0));
    float corn = -1.0*(length(s)-r) ;

    return max(ad, corn);

}


float opIRoundSubtract(in float a, in float b, in float r)
{
    // two lines with space for corner
    vec2 q = vec2(a, b);
    q = mirror2Dv2(q, normalize(vec2(1,1)));
    q.y -= r;
    q.y = min(0.0, q.y);
    float ad = sign(q.x)*length(q);

    // inverted round corner
    vec2 s = vec2(min(a, 0.0), max(b, 0.0));
    float corn = -1.0*(length(s)-r) ;

    return max(ad, corn);    
}

// intersection version, causes some distortion in exterior distances
float opIRoundIntersect(in float a, in float b, in float r)
{
    return opIRoundSubtract(a, -b, r);  
}


// from https://mercury.sexy/hg_sdf/
float fOpDifferenceRound (float a, float b, float r) {    
	vec2 u = max(vec2(r + a,r - b), vec2(0));
	return min(-r, max (a, -b)) + length(u);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float rad = (0.5 + 0.5*sin(iTime)) * 0.2;

    vec2 pos = p;
    pos.y -= 0.5;
	float d1 = sdBox( pos, vec2(0.5, 0.4) );
    float d = opIRoundSubtract( sdBox( p, vec2(1.0, 0.5) ), d1, rad);

    //uncomment to visualize the corner SDF
    //d = sdinvCircle(p, rad);

	// coloring, iq style
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    if( iMouse.z>0.001 )
    {
    pos = m;
    pos.y -= 0.5;
	float d1 = sdBox( pos, vec2(0.5, 0.4) );
    d = opIRoundSubtract(sdBox( m, vec2(1.0, 0.5) ), d1, rad);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}
