#define PI acos(-1.)
#define N(x) fract(sin((x)*33333.333))

mat3 lookat(vec3 eye, vec3 target, vec3 up){
    vec3 w=normalize(target-eye), u=normalize(cross(w,up));
    return mat3(u,cross(u,w),w);
}

vec3 rot(vec3 p,vec3 a,float t) 
{
	a=normalize(a);
	return mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a);
}

float lpNorm(vec2 p, float n){
	p = pow(abs(p), vec2(n));
	return pow(p.x+p.y, 1./n);
}

float map(vec3 p){
	p=rot(p,vec3(1,6,-3),iTime*.3);
	p=rot(p,vec3(5,2,3),iTime*.2);
    p=abs(p);
    if(p.x<p.z)p.xz=p.zx;
	if(p.y<p.z)p.yz=p.zy;
 	if(p.x<p.y)p.xy=p.yx;
 	vec3 nor=normalize(vec3(int(iTime*2.0)%3+3,int(iTime*1.5)%2+2,int(iTime*2.5)%3));
 	float x=dot(p,nor)-.1*fract(sin(iTime+.3*sin(.5*iTime))*2.-1.3);
 	float y=length(cross(p,nor));
 	return min(
        length(vec2(x-clamp(x,.8,1.7),y))-.1,
 		lpNorm(vec2(x-1.1,y-.15),5.)-.07
    );
}

vec3 calcNormal(vec3 p){
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 bg=vec3(1,1.5,1.8)*.6;
    vec3 ro0=vec3(0,0,1.8);
    vec3 ta=vec3(.1,.05,0);
    vec3 rd0=normalize(vec3(p,1.));
    vec3 sv=normalize(cross(ta-ro0,vec3(0,1,0)))*.03;
    float b=6.;  
	for (float j=0.; j<b; j++){
        vec3 q,ro = ro0+sv;
        vec3 rd = lookat(ro,ta,vec3(0,1,0)) * rd0;
		float z = 0., d, i, ITR=50.;
 		for( i = 0.; i < ITR; i++){
            vec3 q = ro + rd * z;
    		z += d = map(q)+N(d)*.1*(1.-exp(-1.5*dot(q,q)));;
    		if(d < .001 || z > 30.) break;
  		}
    	if(d < .001){
	  		vec3 p = ro + rd * z;
	 		vec3 nor = calcNormal(p);
    		vec3 li = normalize(vec3(1));
        	vec3 col = vec3(.7,.5,.4)*2.;
        	col *= pow(1.-i/ITR,2.); 
       	 	col *= clamp(dot(nor,li),.3,.8);
        	col *= max(.5+.5*nor.y,0.);
            float rimd = pow(clamp(1.-dot(reflect(-li,nor),-rd),0.,1.),2.5);
		    float frn = rimd+2.2*(1.0-rimd);
    	    col *= frn*.5;
        	col += pow(clamp(dot(reflect(normalize(p-ro),nor),li),0.,1.),30.);
        	col *= exp(-z*z*0.08);
         	col = min(vec3(1),col*2.);
        	bg += col;
    	}
        sv=rot(sv, normalize(ta-ro0), PI*2./b);
	}
    bg = clamp(bg/b,0.,1.);
    fragColor.xyz = pow(bg,vec3(1.2));
}
