void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.y;
    if(iMouse.z == 0.){
        o_trmono_nooffset = vec2(0.1, 0.0);
    }
    float n_ratio = (iResolution.x / iResolution.y);
    float n_channels = 3.;
    float n_res = floor(2000.*o_trmono_nooffset.x);
    vec2 o_res = vec2(n_res*n_channels, n_res/n_ratio);

    vec2 n_sc = vec2((vec2(1.)/iResolution.xy) * o_res);
    vec2 o_scale = n_sc;

    vec2 o_fragCoord_scaled = ((fragCoord.xy)*o_scale);
    vec2 o_iResolution_scaled = (iResolution.xy * o_scale);

    vec2 o_fragCoord_scaled_floor = floor(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_floor = floor(o_iResolution_scaled);
    vec2 o_fragCoord_scaled_fract = fract(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_fract = fract(o_iResolution_scaled);



    vec2 o_trpino_scldfloor = (o_fragCoord_scaled_floor.xy - o_iResolution_scaled_floor.xy*0.5)/ o_iResolution_scaled_floor.y;



    float n_index = o_fragCoord_scaled_floor.y * o_iResolution_scaled_floor.x + o_fragCoord_scaled_floor.x;
    float n_index_nor = n_index / (o_iResolution_scaled_floor.x * o_iResolution_scaled_floor.y);

    float n_index_channel = mod(n_index,3.);
    float n_index_pixel = floor(n_index_channel /3.);

    vec2 o_pixel = vec2(floor(o_fragCoord_scaled_floor.x/3.), o_fragCoord_scaled_floor.y);

//texelFetch(iChannel0, ivec2(int(uv.x * 512./scale), 0), 0);
    vec4 o_texture_col = texelFetch(iChannel0, ivec2(o_pixel), 0);
    fragColor = vec4(
        o_texture_col.r*float(n_index_channel == 0.),
        o_texture_col.g*float(n_index_channel == 1.),
        o_texture_col.b*float(n_index_channel == 2.),
        1.
    );

}
