void mainImage( out vec4 c, vec2 p)
{
    vec3 r = iResolution;
    for( c*=0.; r.z++ < 6.; ) 
        c += .01 / abs( length(   ( p+p - r.xy ) / min(r.x,r.y) 
                                - .5* cos( iTime * r.z + vec2(0,11) ) 
                              ) - .2);
}
