// not real smoothfract, kind of...
float smoothfract(in float x)
{
    float y;
    // fun too with fract only...

    //return fract(x);

    x=fract(x);
    y=sin((x+0.3*sin(-x*x*3.1415926))*3.1415926);
    y=y*y;
    return y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;

    float pi=3.1415926;

    vec3 col=vec3(uv,0.);

    vec2 c=vec2(10.*sin(iTime*1.23/100.0),10.0*sin(iTime*2.12/100.0));

    // inspired from vibrating plate
    float a=sin(c.x*pi*uv.x)*sin(c.y*pi*uv.y)-sin(c.y*pi*uv.x)*sin(c.x*pi*uv.y);


    col=vec3(smoothfract(a),smoothfract(a*0.8),smoothfract(a*0.9));


    fragColor = vec4(col,1.0);
}
