float shift(float val, float amt)
{
    return round(val/pow(10.0, amt));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.0 - uv.y;
    uv *= vec2(70.0, 40.0);
    uv = floor(uv);
    float u = uv.x - 36.0;
    float v = 18.0 - uv.y;
    float h = pow(u, 2.0) + pow(v, 2.0);
    vec3 col = vec3(0.0);

    if (h < 200.0)
    {
        col = vec3(420.0, 0.0, 520.0);
        float t = 5000.0 + 8.0 * h;
        float p = shift(t * u, 2.0);
        float q = shift(t * v, 2.0);
        float s = 2.0 * q;
        float w = shift(1000.0 + p - s, 2.0) + 8.0;
        if (w > 0.0)
        {
            col.r += pow(w, 2.0);
        }

        float o = s + 2200.0;
        col.r = shift(col.r * o, 4.0);
        col.b = shift(col.b * o, 4.0);

        if (p > -q)
        {
            w = shift(p + q, 1.0);
            col.r += w;
            col.b += w;

        }

    }
    else
    {
        if (v < 0.0)
        {
            col = vec3(150.0 + 2.0 * v, 0.0, 50.0);
            float p = h + 8.0 * pow(v, 2.0);
            float c = (240.0 * -v) - p;
            if (c > 1200.0)
            {
                float o = shift(6.0 * c, 1.0);
                o = c * (1500.0 - o);
                o = shift(o, 2.0) - 8360.0;
                col.r = shift(col.r * o, 3.0);
                col.b = shift(col.b * o, 3.0);
            }

            float r = c + u * v;
            float d = 3200.0 - h - 2.0 * r;
            if (d > 0.0)
            {
                col.r = col.r + d;
            }
        }
        else
        {
            float c = uv.x + 4.0 * uv.y;
            col.r = 132.0 + c;
            col.b = 192.0 + c;
        }
    }

    col.r = min(col.r, 255.0);
    col.b = min(col.b, 255.0);
    col.g = shift(7.0 * col.r + 3.0 * col.b, 1.0);
    col /= 255.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}
