void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ndc = 2.0 * uv - 1.0;
    vec2 ndc2 = ndc;
    ndc2.y /= iResolution.x / iResolution.y;
    float l = length(ndc);
    float l2 = length(vec2(2.0 *ndc.x, ndc.y));
    float l3 = length(ndc2);
    float a = abs(atan(ndc2.y, ndc2.x));
    vec3 col = vec3(1.0 - smoothstep(0.99, 1.0, l) + smoothstep(1.0, 1.01, l));
    col = mix(vec3(1.0 - smoothstep(0.99, 1.0, l2) + smoothstep(1.0, 1.01, l2)), col, 1.0-col.x);
    col *= 1.0 / (1.0 + min((l2 - 0.5) * (l2 - 0.5), l * l));
    if(l > 1.0) col = vec3(0.0);
    else if(l3 < 0.15) col = vec3(0.2/(1.0 + 100. * l3 * l3));
    else if (l2 < 1.0) {
        col *= exp(-l2 * l2);
        col += vec3(0.05) + 0.05 * vec3(sin(a - l * l), cos(a + l2 * l2), sin(a * a -l3 * l3));
        col += 0.05 * vec3(1.0 - smoothstep(0.99, 1.0, l3 - sin(100. * a)) + smoothstep(1.0, 1.01, l3 + sin(100. * a)));
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
