renderpass.code:
uniform float time;
uniform float resolution ;


float hexSdf(vec2 p) {
        float scale = abs(cos(iTime))+1.0;
        p.x *= 0.57735*2.0;
        p.y += mod(floor(p.x/scale), 2.0)*0.5*scale;
        p = abs((mod(p/scale, 1.0) - 0.5));
        return abs(max(p.x*1.5 + p.y, p.y*2.0) - 1.0);
}


vec3 palette(float i) {
        return vec3(1.0, 1.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
		float d = 1.0-hexSdf(fragCoord.xy/100.0);
        fragColor.rgb = vec3(d);
        fragColor.a = 1.0;
        float v_aa1 =  0.96 - (0.07 / 10.);
        float v_aa2 =  0.99 + (0.1 / 10.);
        fragColor = smoothstep(v_aa1, v_aa2, fragColor);
}
