void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;
    vec2 uv0 = uv;
    vec3 finCol = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++) {
        uv = fract(uv*2.5)-0.5;  
        float d = length(uv)*exp(-length(uv0));
        vec3 col = 0.5*cos(iTime+uv.xyx+vec3(0,2,4)+iTime);
        d = sin(d*5.0 + iTime)/5.0;
        d = pow(0.01/d,1.1);
        finCol += col * d;
    }

    float d2 = length(uv0);
    d2 = sin(d2*5.0 + iTime)/5.0;
    d2 = abs(d2);
    d2 = pow(0.1/d2,1.2);
    vec3 finCol2 = finCol * d2;

    fragColor = vec4(finCol2,0.0);
}
