void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;

    uv.x /= iResolution.y/iResolution.x;

    float pixelScaleUV = 64.0f;
    uv = round(uv * pixelScaleUV)/pixelScaleUV;


    float spiralMask1 = 0.5 + 0.5 * cos(3.0* (atan(uv.y,uv.x) + 3.0 * (0.75 + 0.25 * cos(2.01 * iTime)) * length(uv.xy)) - 5.0 * iTime);
    float spiralMask2 = 0.5 + 0.5 * cos(7.0* (atan(uv.y,uv.x) + 7.0 * (0.50 + 0.50 * cos(1.05 * iTime)) * length(uv.xy)) + 11.0* iTime);

    float a = 0.6;
    float b = 1.0 - a;

    float spiralMaskFinal = a * spiralMask1 + b * spiralMask2;

    float pixelScale = 64.0;
    float pixelizedMask = round(spiralMaskFinal * pixelScale)/pixelScale;

    vec3 colorA = vec3(0.1,0.2,0.3);
    vec3 colorB = vec3(0.3,0.6,0.9);

    vec3 finalColour = mix(colorA,colorB,pixelizedMask);

    // Time varying pixel color
    vec3 col = finalColour;

    // Output to screen
    fragColor = vec4(col,1.0);
}
