vec2 distort(vec2 p){
    // vec2 center=vec2(.5);
    vec2 center=iMouse.xy/iResolution.xy;
    float radius=.9;
    float strength=1.1;

    p-=center;

    float d=length(p);
    // p*=d;

    d/=radius;

    float dPow=pow(d,2.);
    // p*=dPow;

    float dRev=strength/(1.+dPow);
    p*=dRev;

    p+=center;

    return p;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fragCoord/iResolution.xy;
    uv=distort(uv);
    vec4 tex=texture(iChannel0,uv);
    vec4 col=tex;
    fragColor=col;
}
