float boxSDF(vec2 p, vec2 R)
{
    vec2 boxpos = vec2(0, 0);
    return length(max(abs(p - boxpos) - R, 0.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mousePos;

    mousePos = iMouse.xy;
    vec2 uv = (fragCoord / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);
    vec2 uv1 = fragCoord/iResolution.xy;

    float col = boxSDF(uv, vec2(0.5, 0.1));
    col = 1.-smoothstep(0., 0.4, col);

    fragColor = texture(iChannel0,uv1) * col;
}
