#define TWO_PI 6.283185307179586
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float pct = 0.0;

    st = ceil(st*12.)/12.;
    vec2 toCenter = vec2(0.)-st;
    float pct2 = pct;
    pct = (ceil((length(toCenter)*10.)*(sin(iTime*TWO_PI*.9)*.4+.5))/10.);
    pct2 = (ceil((length(toCenter)*10.)*(sin(iTime*TWO_PI*.7)*.4+.5))/10.);

    pct = 0.1/pct;
    pct2 = 0.1/pct2;

    vec3 color = vec3(pct, pct2, 0.492);

    vec3 col = mix(color, vec3(0.060,0.058,0.060), (sin(iTime*TWO_PI)*.4+.5));

	fragColor = vec4(col, 1.0 );
}
