void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.yy;
    vec3 v_Position = vec3(uv,0);

    float u_Radius = 1.0;
    float u_Thickness = 0.03;
    vec4 u_Color = vec4(1);      
    vec3 u_Center = vec3(0);
	vec4 background = vec4(vec3(0.0, 0.0, 160.0)/255.0, 1.0);


    vec3 xyz = v_Position - u_Center;
    float l = length(xyz);        
   	fragColor = u_Color;
    //float a = smoothstep(u_Thickness/u_Thickness/100.0, 0.0, abs(l-u_Radius+u_Thickness)-u_Thickness);
    /*
     * thanks FabriceNeyret2
     */
    float a = smoothstep( 3./ iResolution.y, 0., abs(l-u_Radius+ u_Thickness) - u_Thickness );       
    fragColor.a = a;
    fragColor = mix(background,fragColor, fragColor.a );
}
