void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 uv = uv0;
    uv *= 2.;

    float t = radians(5.) * iTime;
    uv = mat2(sin(t), cos(t), -cos(t), sin(t)) * uv;

    uv = sin(radians(220.) * uv);
    t = radians(90.) * iTime;
    float t2 = radians(170.) * iTime;
    uv = uv + .2 * vec2(cos(t2), sin(t2));
    uv = mat2(sin(t), cos(t), -cos(t), sin(t)) * uv;
    float t3 = radians(170.) * iTime;
    uv = uv + .1 * vec2(cos(2. * t3), sin(3. * t3));

    uv = uv + vec2(.1, 0) * sin(iTime);
    uv = mix(uv, cos(uv), .5 + .5 * sin(radians(5.) * iTime));

    float r0 = .03 + .02 * sin(radians(37.) * iTime);
    float d0 = abs(dot(uv, vec2(.1, 0.))) - r0;
    float r1 = .06 + .04 * sin(radians(21.) * iTime);
    float d1 = abs(length(uv) - 1.) - r1;
    float d = mix(d0, d1, .75);

    float a = smoothstep(1.5, -1.5, d * iResolution.y / 4.);
    vec3 col = mix(vec3(0.), 0.5 + 0.5*cos(iTime+uv0.xyx+vec3(0,2,4)), a);
    fragColor = vec4(col,1.0);
}
