float light(vec2 uv){
    uv.y +=(cos(iTime)*sin(iTime));
    uv.x +=cos(sin(1.-iTime)+40.)+0.5;
    return length(uv)-.3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float a_r = iResolution.x/iResolution.y;
  vec2 uv = (2.*fragCoord-iResolution.xy ) / iResolution.y ;
  vec2 uv2 = uv;

  uv *= 4.*a_r; 
  uv.x += step(1., mod(uv.y,2.)) ;
  uv = fract(uv)-0.5;
  float d = smoothstep(0.,10.*3./ iResolution.y,length(uv)-0.4);
  float l = light(uv2); 
  uv +=(1.-l * 3.) ;
  float d2;
  d2 =  smoothstep(0.,0.2*a_r,length(uv) ) * abs(l/3.);
  d2 +=  smoothstep(0.,0.25*a_r,length(1.-fract(uv)-0.5)-.19) * l*2.;
  fragColor = vec4(vec3(1.-(d+d2/2.)),1.0);
}
