const float PI = 3.14;

float steps = 12.0;
vec3 HUEtoRGB(in float hue)
{
    // Hue [0..1] to RGB [0..1]
    // See http://www.chilliant.com/rgb2hsv.html
    vec3 rgb = abs(hue * 6. - vec3(3, 2, 4)) * vec3(1, -1, -1) + vec3(-1, 2, 2);
    return clamp(rgb, 0., 1.);
}
vec3 HSVtoRGB(in vec3 hsv)
{
    // Hue-Saturation-Value [0..1] to RGB [0..1]
    vec3 rgb = HUEtoRGB(hsv.x);
    return ((rgb - 1.) * hsv.y + 1.) * hsv.z;
}


vec2 applyTransformation(vec2 pos,float iteration){

    float angle = atan(pos.y,pos.x);
    float initialDist = length(vec3(pos,1)) * (iteration+1.0);
    float currentStep = mod(floor(initialDist * steps),steps);
    vec2 outVec = vec2(1);
    float sinDist = (sin(angle*(3.0 * currentStep) + iTime * sin(currentStep))*0.05 + 1.0);

    outVec.xy = pos.xy * sinDist;

    return outVec;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = vec2(0.5,0.5) + ((iMouse.xy==vec2(0.)) ? vec2(.5,.5) : iMouse.xy/iResolution.xy)-vec2(0.5);

    vec2 offset = (uv - center) * 1.8;
    offset.y *= iResolution.y/iResolution.x;

    offset *= 4.5;

   //clamp keeps 1 & 2 'large' pattern explosions out   for(float i = 0.0; i < clamp((mod(fract(iTime)*abs(iMouse.x+1.)*1000.0,37.0)),3.0,37.0); i++){
    for(float i = 0.0; i < (mod(fract(iTime)*abs(iMouse.x+1.)*1000.0,37.0)); i++){
       offset = applyTransformation(offset,i/0.1);
    }


    float initialDist = length(offset);
    float currentStep = mod(floor(initialDist * steps*fract(iTime)*10.),steps);


    float clampedDist = step(fract(length(offset)*steps),0.5);


    vec3 col = HSVtoRGB(vec3(currentStep/steps,1,1));

    // Output to screen
    fragColor = vec4(col,1.0);
}
