void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Sweeping Line
    // Wavelength
    float lam = 4.0;
    // Period
    float period = 6.0;
    float lin = 0.02 / (2.0 * abs(sin(uv.x * (2.0*3.14 / lam) - (2.0*3.14/period) * iTime)));

    // Circles
    float speed = 1.5;
    float line_scale = 2.5;
    // Circle 1
    vec2 pnt_1 = vec2(0.5, 0.5);
    float cir_1 = 0.02 / (line_scale * abs(sin(length(uv - pnt_1)* (2.0*3.14 / (speed * period))
        - ((2.0*3.14/period) * iTime - pnt_1.x * (2.0*3.14 / lam)))));
    // Circle 2
    vec2 pnt_2 = vec2(0.75, 0.25);
    float cir_2 = 0.02 / (line_scale * abs(sin(length(uv - pnt_2)* (2.0*3.14 / (speed * period))
        - ((2.0*3.14/period) * iTime - pnt_2.x * (2.0*3.14 / lam)))));
    // Circle 3
    vec2 pnt_3 = vec2(1.25, 0.75);
    float cir_3 = 0.02 / (line_scale * abs(sin(length(uv - pnt_3)* (2.0*3.14 / (speed * period))
        - ((2.0*3.14/period) * iTime - pnt_3.x * (2.0*3.14 / lam)))));

    // Output to screen
    fragColor = vec4(lin + cir_1, lin + cir_2, lin + cir_3, 1.0);

}
