vec3 palette( float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(1.+sin(mod(iTime,6.))*.1263, cos(mod(iTime,6.)*.00116), 1.+sin(3.1415926*mod(iTime,6.))*0.1);
    return a + b*cos(3.14159265358979323846264338327950288*0.5*(c*t*d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float M_PI = 3.14159265358979323846264338327950288;
    float sinFactor = 12.55;

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    uv=fract(uv *2.5) +0.5 + sin(sin(mod(iTime,6.)));

    float d = length(2.25*M_PI*sin(sin(tan(mod(iTime,60.)*0.01)*0.1*mod(iTime,60.))*uv0));

    vec3 col = palette(length(uv0) + mod(iTime,60.));


    d = sin(d * sinFactor)/cos( 0.1*sin(0.1*mod(iTime,60.))*(M_PI ))*-sqrt(sinFactor);
    d=abs(d);

    d = 0.55 / d;

    col*=d;

    fragColor = vec4(col, 1);
}
