void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Convert the fragment coordinate to Shader Toy's coordinate system
    vec2 stCoord = fragCoord / iResolution.xy; 

    // Define the dimensions of the rectangle
    vec2 rectSize = vec2(1.0, 2.0); 

    float t = clamp(iTime - 0.5, 0.0, 1.2); // Clamp time between 0 and 1
    float currentValue = mix(6.0, 0.0, t);

    // Center the rectangle on the screen
    vec2 rectCenter = vec2(sin(sin(currentValue * iTime * 1.0) * iTime * 1.7) + 0.1, 0.5);

    // Calculate the minimum and maximum coordinates of the rectangle
    vec2 rectMin = rectCenter - rectSize * 0.5;
    vec2 rectMax = rectCenter + rectSize * 0.5;

    // Check if the current fragment is inside the rectangle
    if (stCoord.x > rectMin.x && stCoord.x < rectMax.x &&
        stCoord.y > rectMin.y && stCoord.y < rectMax.y)
    {
        // Set the color of the rectangle
        fragColor = vec4(0.0, 0.0, 1.0, 0.0); 
    }
    else
    {
        // Set the background color
        fragColor = vec4(0.0, 0.0, 1.0, 0.0); 
    }


    // Center the rectangle on the screen
    if(iTime <= 5.45)
    {
        vec2 rectSize2 = vec2(1.0, 2.0); 

    float t2 = clamp(iTime - 0.5, 0.0, 1.2); // Clamp time between 0 and 1
    float currentValue2 = mix(6.0, 0.0, t2);


    vec2 rectCenter2 = vec2(atan(sin(currentValue2 * iTime * 1.53) * iTime * 2.4) + 1.45, 0.5);

    // Calculate the minimum and maximum coordinates of the rectangle
    vec2 rectMin2 = rectCenter2 - rectSize2 * 0.5;
    vec2 rectMax2 = rectCenter2 + rectSize2 * 0.5;

    // Check if the current fragment is inside the rectangle
    if (stCoord.x > rectMin2.x && stCoord.x < rectMax2.x &&
        stCoord.y > rectMin2.y && stCoord.y < rectMax2.y)
    {
        // Set the color of the rectangle
        fragColor = vec4(0.0, 1.0, 0.0, 0.0); 
    }
    }


    if(iTime >= 3.55 && iTime <= 5.00)
    {
        fragColor *= 0.9+0.1*sin(10.0*iTime+stCoord.y*785.0);
        fragColor *= 0.99+0.01*sin(110.0*iTime);
    }
}
