void mainImage(out vec4 fragColor, in vec2 fragCoord )
{

    float pixelSize = 6.0;

    vec3 col = texture(iChannel0,fragCoord/iResolution.xy).rgb;
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 srcSize = vec2 (1.0 / iResolution.x, 1.0 / iResolution.y);

    // Get pixel that fragCoord is in
    vec2 pixels = trunc(iResolution.xy / pixelSize);
    vec2 pixelCoord = fragCoord.xy / pixelSize;

    // Location inside of pixel
    vec2 temp = pixelCoord - trunc(pixelCoord);
    vec2 loc = round(temp * pixelSize);

    vec3 avg = vec3(0.0);
    int count = 0;

    for(float j = -loc.x; j < pixelSize - loc.x - 1.0; j++){
        for(float i = -loc.y; i < pixelSize - loc.y - 1.0; i++){
            avg += texture(iChannel0, uv + (vec2(j, i) * srcSize)).rgb;
            count++;
        }
    }

    avg /= float(count);

    // Output to screen
    fragColor = vec4(avg,1.0);
}
