vec3 palette1( float t ) {
    //by Inigo Quilez
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(0.5,0.5,0.5);
    vec3 d = vec3(0.1,0.1,0.2);

    return vec3(a + b*cos(6.28318530718*(c*(t) + d)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5)*2.0;

    vec2 dummyuv = uv;
    float weight = 1.0 - sin(iTime/3.0);
    int currentIndex = int(iTime/3.0);



    for (int i = 0; i < currentIndex; i++){uv.x += cos(uv.y*4.0 + uv.x*5.0); uv.y += sin(uv.y*4.0- uv.x/5.0);}
    dummyuv = uv; dummyuv.x += cos(dummyuv.y*4.0 + dummyuv.x*5.0); dummyuv.y += sin(dummyuv.y*4.0- dummyuv.x/5.0);
    vec3 color = vec3(0.0);

    uv = weight*uv + (1.0-weight)*dummyuv;

    float d = length(uv - vec2(uv.y,uv.x));
    color += smoothstep(0.03,0.02,d);
    color.rg = exp(uv);
    // Output to screen
    fragColor = vec4(color,1.0);
}
