// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}



float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}



float sdPlane( vec3 p, vec3 n, float h )
{
  // n must be normalized
  return dot(p,n) + h;
}






float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}





float sdCutHollowSphere( vec3 p, float r, float h, float t )
{
  // sampling independent computations (only depend on shape)
  float w = sqrt(r*r-h*h);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  return ((h*q.x<w*q.y) ? length(q-vec2(w,h)) : 
                          abs(length(q)-r) ) - t;
}




float BallGyroid(vec3 p) {
    p = p*20.;
    return dot(sin(p), cos(p.yzx + 2.0*iTime))/20.;
}

float GetDist(vec3 p) {
    float d = sdSphere(p, 1.0);

    float h = sdCutHollowSphere(p, 1.,  0.1+1.1*sin(iTime), 0.06);
    float h2 = sdCutHollowSphere(-p, 1., 0.1-1.1*sin(iTime), 0.06);

    float h3 = sdCutHollowSphere(p, 1.,  0.3+1.1*sin(iTime), 0.0001);
    float h4 = sdCutHollowSphere(-p, 1., 0.3-1.1*sin(iTime), 0.0001);

    float d2 = max(d, h3);
    d2 = max(d2,h4);

    d = max(d,h);
    d = max(d,h2);

    d = min(d,d2*0.6);

    //d = min(d, opExtrussion(p, d, 1.));

    float e = d*d;
    d = min(d,e);


    float g = BallGyroid(p);
    d = max(d, g);

    float tor = sdTorus(vec3(p.x,p.y + sin(iTime),p.z), 0.7*vec2(cos(2.0*iTime)+1.0,0.1));

    tor = min(tor, tor*tor);

    d = min(d,tor);

    float s = sdSphere(vec3(-1.0+cos(iTime)+p.x,p.y,p.z), 0.3);
    float s2 = sdSphere(vec3(1.0+cos(iTime+ 3.141529)+p.x,p.y,p.z), 0.3);


    float s3 = min(s*s,s2*s2);

    d = min (d, s3);
    //d = min (d, s2);

    //float gnd = p.y + 1.5;

    //d =  min(d,gnd);

    float c = sdCappedCylinder(vec3(p.x,0.1*p.y-0.31- 0.03*cos(2.0*iTime),p.z),0.2,0.1);
    float c2 = sdCappedCylinder(vec3(p.x,0.1*p.y+0.31+ 0.03*cos(2.0*iTime),p.z),0.2,0.1);

    d = min(d, c*c);
    d = min(d, c2*c2);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3)*1.0;
    ro.yz *= Rot(PI-1.0);//Rot(0.2*cos(2.0*iTime)-0.5);//Rot(-m.y*PI+1.);
    ro.xz *= Rot(iTime*1.5);//Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);



    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);
        float cd = length(p);

        float dif = dot(n, -normalize(p))*.5+.5;
        col = vec3(dif);


        //col *= 1.0/(cd);


    } else {col = vec3(0.1,0.1,0.1);}

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
