void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = fragCoord;   
    fragColor = vec4(0.0,1.0,1.0,1.0);
    vec2 rep = vec2(50.,40.); // размер тайла

    p.x = mod(p.x, rep.x); // Repeats x axis
    p.y = mod(p.y, rep.y); // Repeats y axis

    if (p.x >= 10.0 && p.x <= 40.0) {
         if (p.y >= 15.0 && p.y < 35.0) {
               fragColor = vec4(1.0,0.0,0.0,1.0);
         }      
    }
}
