float N21(vec2 p) {
    return fract(sin(p.x * 123.21 + p.y * 982.1) * 920.11);
}

float smoothNoise(vec2 uv) {
   vec2 st = fract(uv);
   st = st * st * (3. - 2. * st);

   vec2 id = floor(uv);
   float bl = N21(id);
   float br = N21(id + vec2(1., 0.));
   float b = mix(bl, br, st.x);

   float tl = N21(id + vec2(0.0, 1.0));
   float tr = N21(id + vec2(1.0, 1.0));
   float t = mix(tl, tr, st.x);

   return mix(b, t, st.y);
}

float smoothNoise2(vec2 uv) {
   float m = smoothNoise(uv * 4.);
   m += smoothNoise(uv * 8.) * .5;
   m += smoothNoise(uv * 16.) * .25;
   m += smoothNoise(uv * 32.) * .125;
   m += smoothNoise(uv * 64.) * .0625;
   m /= 2.;
   return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
   vec3 col = vec3(0.);
   uv += iTime * .1;
   float m = smoothNoise2(uv);
   col = vec3(m);
   fragColor = vec4(col, 1.);

}
