float msc(vec2 uv)
{
    return length(vec2( mod(sin(uv.y) , cos(uv.x)), mod(sin(uv.x) , cos(uv.y))));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    uv /= dot(uv,uv);
    vec3 col = vec3(msc(uv * mod(iTime,60.0+iDate.z) + vec2(cos(iTime),sin(iTime))));
    col *= 0.5 + 0.5*sin(iTime+uv.xyx+vec3(100,16,80));
    col = pow(col,vec3(0.454545));
    fragColor = vec4(col,1.0);
}
