void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    

	fragColor = texture(iChannel0, uv);
}

mat3 mats[9];

//inverse Gaussian activation function
float ig(float x) {
  return -1./pow(2., (0.6*pow(x, 2.)))+1.;
}

float cutit(float inp){
return clamp(inp, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//The configuration tensor (change these values for different patterns):
mats[0] = mat3(.68, 0., -.05, -.05, .68, .0, 0., -.05, .68);
mats[1] = mat3(-.9, 0.05, 0., 0., -.9, 0.05, 0.05, 0., -.9);
mats[2] = mat3(.68, 0., -.05, -.05, .68, .0, 0., -.05, .68);
mats[3] = mat3(-.9, 0.05, 0., 0., -.9, 0.05, 0.05, 0., -.9);
mats[4] = mat3(-.66, -.01, .01, .01, -.66, -.01, -.01, .01, -.66);
mats[5] = mat3(-.9, 0.05, 0., 0., -.9, 0.05, 0.05, 0., -.9);
mats[6] = mat3(.68, 0., -.05, -.05, .68, .0, 0., -.05, .68);
mats[7] = mat3(-.9, 0.05, 0., 0., -.9, 0.05, 0.05, 0., -.9);
mats[8] = mat3(.68, 0., -.05, -.05, .68, .0, 0., -.05, .68);






   vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 rtot = vec3(0.,0.,0.);

    int ind = 0;


//the convolution kernel is just the Moore neighborhood of a given cell
//take the RGB values as vectors and apply a matrix transform specified via the configuration tensor, then sum
    for(float i = -1.0; i <= 1.0; i += 1.0)
    {
        for( float j = -1.0; j <= 1.0; j += 1.0)
        {
        	vec2 offset = vec2(i, j) / iResolution.xy;		 
        	rtot += mats[ind]*texture(iChannel0, uv + offset).rgb;
            ind++;
        }
    }



    vec3 rfin = vec3(cutit(ig(rtot.x)), cutit(ig(rtot.y)), cutit(ig(rtot.z)));
    if(iFrame <50){
    rfin = vec3(cutit(rtot.x), cutit(rtot.y), cutit(rtot.z));
    }


//initialize random colors for each cell    
    if(iFrame < 10) {
    rfin = vec3(mod(pow(uv.x*uv.y*iDate[3], 7.),10.)/10., mod(pow(uv.x*uv.y*iDate[3], 6.),10.)/10., mod(pow(uv.x*uv.y*iDate[3], 5.),10.)/10.);
    }

//paint on pixels manually with the mouse
    if(iMouse.z > 0.0 && abs(fragCoord.x-iMouse.x)<1.0 && abs(fragCoord.y-iMouse.y)<1.0){
    rfin = vec3(1.0,1.0,1.0);
    }



    fragColor = vec4(rfin,1.0);
}

//anti-flicker buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
    vec4 col1 = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, uv);
    fragColor = (col1+col2)*.55;
}
