#define PI 3.1415926535

float warble = 2.0;
float sampleSize = 20.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord / iResolution.xy;
    vec2 kernelStepSize = 1.0 / iResolution.xy;

    vec2 mouse = vec2(iMouse)/vec2(iResolution);

    warble = 10.0 + mouse.y;
    sampleSize =  12.0 + mouse.x;

    vec2 tileRotDir = (step(0.5, mod((sampleSize * uv),1.0)));
    vec2 centerTiledSt = mod((sampleSize * uv),1.0) - 2.0 * (tileRotDir - 1.0);
    float rotDir = (step(0.3, length(centerTiledSt)));// - 2.0*( 1.0 - (step(0.4, length(centerTiledSt))));
    tileRotDir *= rotDir;

    vec2 samplePoint = uv + kernelStepSize * warble * tileRotDir * vec2(1.1 * cos(2.0* iTime), sin(2.0*iTime));
    fragColor = texture( iChannel0, samplePoint);
}
