float shadow(float time, vec2 uv)
{
    float velx=1.0;
    float freqx=1.0;
    float vely=1.0;
    float freqy=0.5;
    float timeshiftx = cos(iTime*velx) + (uv.x*freqx);
    float timeshifty = sin(iTime*vely) + (uv.y*freqy);
    float shadow = 0.5+(timeshiftx)*(timeshifty)*0.5;
    return shadow;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wiggle = sin(iTime*2.0+(uv.y*5.0))*0.02;

     // Output to screen
    if(uv.x>0.7+wiggle)
    {
      fragColor = vec4(1.0,0,0.1,0)*shadow(iTime,uv);
    }
    else
    {
      fragColor = vec4(1,1,1,1)*shadow(iTime,uv);
    }
    if(uv.x<0.3+wiggle)
    {
      fragColor = vec4(0,1,0,0)*shadow(iTime,uv);
    }
}
