float str = 2.;
float ok = 1.5;
const float PI = 3.14159265359;

vec2 warp(vec2 inp)
{
    inp.y -= (inp.y - .5)* str * pow(abs(inp.x - .5), ok);
    return inp;
}

//adds these circles on screen to show how it works
vec4 col(vec2 inp)
{
    float x = inp.x + iTime/100.; float y = inp.y;
    vec4 timeDistortion = vec4(0, 1, 2, 0);
    vec4 clr = vec4(sin(inp.y * iResolution.y * .5) + sin(inp.x * iResolution.x * .5));

    clr *= abs(sin(iTime + (timeDistortion + x) * PI / 3.)) * y;

    return clr;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = warp(fragCoord/iResolution.xy);
    vec2 cntr = vec2(0);

    uv.x -= iTime/100.;
    fragColor = col(uv);
}
