#define TIME            iTime

vec2 N(float angle){
    return vec2(sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    uv *= .9+sin(iTime/iResolution.y);

    vec3 col =vec3(0) ;

    uv.x = abs(uv.x);


    vec2 n = N((5./6.)*3.1415);
    float d = dot(uv-vec2(.5,0), n);
    uv -= n*max(0.,dot(uv, n))*2.;

  n = N((2./3.)*3.1415);
  float scale = 1.;
  uv.x += .5;
 for(int i=0; i<70; i++) {
    uv *= 3.;
    scale *= 3.;
    uv.x -= 1.5;

    uv.x = abs(uv.x);
  //  uv.x -= .5;
    uv -= n*min(0.,dot(uv, n))*2.;

    }

   d = length(uv - vec2(clamp(uv.x, -1., 1.),0));
    col += smoothstep(1./iResolution.y, .0, d/scale);
    uv /= scale;
    col += texture(iChannel0, uv*2.+iTime*.1).rgb;

    fragColor = vec4(col,1.0);
}
