void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );
	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;

	vec4 nyan = texture( iChannel0, uv );

    vec2 p = fragCoord.xy / iResolution.xy;
    // Wavy effect based on x position and time
    float waveOffset = 0.02 * sin(p.x * 10.0 + iTime * 5.0);

    vec3 rainbowCol = vec3(0.0);
    float rainbowWidth = 0.24;
    float rainbowStart = 0.0;
    float rainbowEnd = 0.58 + rainbowWidth;
    float yOffset = 0.39;  // Adjust this value to raise/lower the rainbow
    float adjustedY = p.y - yOffset + waveOffset;

    if(p.x > rainbowStart && p.x < rainbowEnd)
    {
        float bandSize = rainbowWidth/6.0;

        if(adjustedY >= 0.0 && adjustedY < 1.0 * bandSize) 
            rainbowCol = vec3(0.8, 0.0, 0.0);      // red
        else if(adjustedY >= 1.0 * bandSize && adjustedY < 2.0 * bandSize) 
            rainbowCol = vec3(0.8, 0.8, 0.0); // yellow
        else if(adjustedY >= 2.0 * bandSize && adjustedY < 3.0 * bandSize) 
            rainbowCol = vec3(0.0, 0.8, 0.0); // green
        else if(adjustedY >= 3.0 * bandSize && adjustedY < 4.0 * bandSize) 
            rainbowCol = vec3(0.0, 0.8, 0.8); // cyan
        else if(adjustedY >= 4.0 * bandSize && adjustedY < 5.0 * bandSize) 
            rainbowCol = vec3(0.2, 0.0, 0.8); // blue
        else if(adjustedY >= 5.0 * bandSize && adjustedY < 6.0 * bandSize) 
            rainbowCol = vec3(0.8, 0.0, 0.4); // purple
    }

    vec3 col = mix(rainbowCol, nyan.xyz, nyan.w);

    fragColor = vec4( col, 1.0 );
}
