void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    vec2 dot1 = vec2(-0.35, -0.22);
    vec2 dot2 = vec2(0.45, 0.37);


    float m = 0.0;

    m += step(length(uv - dot1), 0.01);
    m += step(length(uv - dot2), 0.01);

    vec2 p = mix(dot1, dot2, sin(iTime * 2.) * 0.5 + 0.5);

    float d = min(length(p - dot1), length(p - dot2));

    vec3 cc = cross(vec3(dot1, 0.0) - vec3(dot2, 0.0), vec3(0., 0., 1.));

    p += cc.xy * sin(iTime * 30.0) * 0.3 * d;

    vec2 do3 = p;

    m += step(length(uv - do3), 0.01);

    vec3 col = vec3(m);

    // Output to screen
    fragColor = vec4(col,1.0);
}
