#define TWOPI 6.28318530718
//get herz from bpm
#define H(bpm) bpm/60.0
//get bpm from hertz
#define BPM(h) h*60.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;


    vec3 col = vec3(0.);

    float bpm = BPM(3.0);

    float d = step(0.9, sin(TWOPI*H(bpm)*iTime));

    col += d;

    // Output to screen
    fragColor = vec4(col,1.0);
}
