#define R vec2(1,0)
#define I vec2(0,1)


vec2 cpow(vec2 z, vec2 p) {
    float a = atan(z.y, z.x);
    float lnr = 0.5 * log(dot(z,z));
    float m = exp(p.x * lnr - p.y * a);
    float angle = p.x * a + p.y * lnr;
    return vec2(cos(angle), sin(angle)) * m;
}

vec2 cmul(vec2 z, vec2 c) {
    return vec2(z.x * c.x - z.y * c.y, z.x * c.y + z.y * c.x);
}

vec2 cdiv(vec2 z, vec2 c) {
    return vec2(z.x * c.x + z.y * c.y, z.y * c.x - z.x * c.y) / dot(c,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord.xy - 0.5*iResolution.xy) / -iResolution.y;

    uv *= 2.;

    float t = iTime;
    float tt = tan(t*0.25);    
    float ttt = tan(t*0.3333333);    

    uv = cdiv(uv+-I*ttt,cmul(uv,-I*ttt)+R);
    uv = cdiv(uv+R*tt,cmul(uv,-R*tt)+R);
    uv = dot(uv,uv) > 1. ? cdiv(-R,uv) : uv;
    uv = cmul(1728.*cpow(uv,5.*R),cdiv(cpow(cpow(uv,10.*R)-11.*cpow(uv,5.*R)-R,5.*R), cpow(cpow(uv,20.*R)+228.*cpow(uv,15.*R)+494.*cpow(uv,10.*R)-228.*cpow(uv,5.*R)+R, 3.*R)));
    uv = cpow(uv,0.5*R);
    uv = cdiv(uv-R,uv+R);
    uv = cpow(uv,0.5*R);
    uv = cmul(uv,vec2(cos(t),-sin(t)));

    vec3 col = 0.5 + 0.5 * cos(t*2.+atan(uv.y,uv.x)+vec3(0,1,2));
    vec3 c;
    c = dot(uv,uv) > 1.001 ? c : uv.x > 0. ? vec3(0) : col;
    vec2 ul = uv*2.;
    vec2 uc = ul + vec2(0,1);
    vec2 lc = ul - vec2(0,1);
    c = dot(uc,uc) < 1. ? col : dot(lc,lc) < 1. ? vec3(0) : c;
    vec2 iul = ul * 3.;
    vec2 iuc = iul + vec2(0,3);
    vec2 ilc = iul - vec2(0,3);
    c = dot(iuc,iuc) < 1. ? vec3(0) : dot(ilc,ilc) < 1. ? col : c;

    // Output to screen
    fragColor = vec4(c+tan(c),1.0);
}
